import React, { Component } from 'react';
import '../node_modules/bootstrap/dist/css/bootstrap.css';
import './common.css';
class Dashboard extends Component {
    state = { cust: [], 
              message: "", 
              emp : {id : 0, Address: "", City : "",Email : "",Name : "" , Role:""}}

    componentDidMount()
    {
        var helper = new XMLHttpRequest();
        helper.onreadystatechange = ()=>{
            if(helper.readyState == 4 &&
               helper.status == 200)
               {
                //debugger;
                var data = JSON.parse(helper.responseText);

                console.log("Setting State to Array in Component Did Mount & XHR Call")
                this.setState({emps: data})
               }
        }
        helper.open("GET", 
                    "http://127.0.0.1:9898/emps");
        helper.send();
    }
    onTextChanged=(args)=>{
        var copyOfEmp = {...this.state.emp};
        copyOfEmp[args.target.name] = 
                    args.target.value;
        this.setState({emp:copyOfEmp});
    }

    AddRecord =()=>{
      console.log(this.state.emp);

      var helper = new XMLHttpRequest();
      helper.onreadystatechange = ()=>{
          if(helper.readyState ==4 && 
              helper.status == 200)
              {
                  var result = 
                  JSON.parse(helper.responseText);
                  if(result.affectedRows > 0)
                  {
                     this.ShowMessage("Record Added Successfully!");
                     this.Refresh();
                  }
                  else
                  {
                      this.ShowMessage("Something went wrong!");
                  }
                 // console.log(result);
              }
      }

      helper.open("POST", 
                   "http://127.0.0.1:9898/emps");
      helper.setRequestHeader("content-type","application/json");
      helper.send(JSON.stringify(this.state.emp));
    }
    Refresh=()=>{

        var helper = new XMLHttpRequest();
        helper.onreadystatechange = ()=>{
            if(helper.readyState == 4 &&
               helper.status == 200)
               {
                //debugger;
                var data = JSON.parse(helper.responseText);
                this.setState({emps: data})
                this.setState({emp: {No : 0, 
                                     Name: "", Address: ""}})
               }
        }
        helper.open("GET", 
                    "http://127.0.0.1:9898/emps");
        helper.send();
    }

    ShowMessage=(urmessage)=>{
        this.setState({message:urmessage});
        window.setTimeout(()=>{
            this.setState({message:""});
        }, 5000);
    }

    DeleteRecord =(No)=>{
        //debugger;
        var helper = new XMLHttpRequest();
        helper.onreadystatechange = ()=>{
            if(helper.readyState == 4 &&
               helper.status == 200)
               {
                //debugger;
                var result = JSON.parse(helper.responseText);

                if(result.affectedRows>0)
                {
                    this.Refresh();
                    this.ShowMessage("Deletion successfull!")
                }
                else
                {
                    this.ShowMessage("Something went wrong!");
                }
               }
        }
        helper.open("DELETE", 
                    "http://127.0.0.1:9898/emps/"+No);
        helper.send();

    }

    render() 
    {
        console.log("Inside render with emps size as " + this.state.emps.length );

        return (
        <div className='container'>
            <div className='myDivStyle'>
                    <hr></hr>
                    No <input type='number' 
                          value={this.state.emp.No}
                          name='No'
                          onChange={this.onTextChanged}/>
                    <hr></hr>
                    Name <input type='text' 
                          value={this.state.emp.Name}
                          name='Name'
                          onChange={this.onTextChanged}/>
                    <hr></hr>
                    Address <input type='text' 
                          value={this.state.emp.Address}
                          name='Address'
                          onChange={this.onTextChanged}/>
                    <hr></hr>
                    <button onClick={this.AddRecord}
                            className='btn btn-primary'>
                        Add record
                    </button>
                    <hr></hr>
                </div>
            <hr></hr>
            <div className='table-responsive'>
                <div className='alert alert-danger'>
                    {this.state.message}
                </div>
                <table className='table table-bordered'>
                   <thead>
                        <tr>
                            <th>No</th>
                            <th>Name</th>
                            <th>Address</th>
                            <th></th>
                        </tr>
                   </thead>
                   <tbody>
                        {
                        this.state.emps.map((emp)=>{
                            return <tr key={emp.No}>
                                <td>{emp.No}</td>
                                <td>{emp.Name}</td>
                                <td>{emp.Address}</td>
                                <td>
                <button className='btn btn-danger'
                onClick={()=>{
                    debugger;
                    this.DeleteRecord(emp.No);
                            }}>
                    Delete
                </button>
                                </td>
                            </tr>
                        })
                        }
                   </tbody>
                </table>
            </div>
        </div>);
    }
}

export default Dashboard;
