import React from 'react'

function BookingSummary(props) {
    const [roomData, setRooms] = useState([]);
    const history = useHistory();
    const checkIn = new Date(props.location.state.checkindate);
    const checkOut = new Date(props.location.state.checkoutdate);
    const city = props.location.state.city;

    const calculatePrice = (room) => {
      const pricePerNight = room.price;
      const numberOfNights = Math.ceil((checkOut - checkIn) / (1000 * 60 * 60 * 24));
      return pricePerNight * numberOfNights;
    };

    const showBookingSummary = (room) => {
      const summary = {
        checkIn,
        checkOut,
        price: calculatePrice(room),
        type: room.type,
        roomid: room.roomid
      };
      history.push("/BookingSummary", summary);
    };

    useEffect(() => {
      axios.get('http://localhost:8080/user/CheckAvailability/' + city)
        .then(response => setRooms(response.data))
        .catch(error => console.error('Error fetching room data:', error));
    }, []);

    return (
      <div className="container">
        <h1 className="my-4">Room List</h1>
        <div className="row">
          {roomData.map(room => (
            <div className="col-md-4 mb-4" key={room.roomid}>
              <div className="card">
                {/* Rest of your card content */}
                <div className="d-flex justify-content-between">
                  <button className="btn btn-primary" disabled={room.status !== 0} onClick={() => showBookingSummary(room)}>
                    Book Now
                  </button>
                  <p className="card-text">${room.price} (Total: ${calculatePrice(room)})</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

export default BookingSummary
